s = ""

n = len(s)
A = [0]*(n+2)
B = [0]*(n+2)

for i in range(1,n+1):
    if s[i-1] == 'a':
        A[i] = A[i-1] + 1
    else:
        A[i] = A[i-1] 

for j in range(1,n+1):
    if s[n-j] == 'b':
        B[n-j+1] = B[n-j+2] + 1
    else:
        B[n-j+1] = B[n-j+2]

k = 1

for i in range(0,n+1):
    if A[i] + B[i+1] > k:
        k = A[i] + B[i+1]

print(k)